import React from "react";
import { connect } from 'react-redux';
import { fetchStreams } from '../../actions';
/*const StreamList = () => {
  return <div>StreamList</div>;
};*/

class StreamList extends React.Component {
  componentDidMount() {
    this.props.fetchStreams();
  }

  renderAdmin(stream) {
    if (stream.userId === this.props.currentUserId) {
      return
      return <div>EDIT/DELETE</div>
    }
  }


  renderList() {
    return this.props.streams.map(stream => {
      return (
        <div className="item" key={stream.id}>
          <i className="large middle aligned icon camera" />
          <div className="content">
            {stream.title}
            <div className="description">{stream.description}</div>
          </div>
          {this.renderAdmin(stream)}
        </div>
      );
    });
  }
  render() {
    console.log(this.props.streams);
    return (
      <div>
        <h2>Streams</h2>
        <div className="ui celled list">{this.renderList()}</div>
      </div>);
  }
}
const mapStateToProps = (state) => {

  return {
    streams: Object.values(state.streams),
    currentUserId: state.auth.sserId
  };
};
export default connect(mapStateToProps, { fetchStreams })(StreamList);
// can put null first export default connect(null,)(StreamList);


//class compoent beacuse wanna call action creater in life cycle method




// leave them as a object form in reducer because it make it easily to update add and delete records
//but when we takes these streams and get them into a component through mapstatetoprops usually turn the object into an actual array  , make sure easy makeover  and map function
