import React from "react";
import Modal from '../Modal';
import history from '../../history';
class StreamDelete extends React.Component {
  //cannot assume that the stream has already been loaded up
  componentDidMount() {
    console.log(this.props);
  }
  renderActions() {
    const actions = (
      <React.Fragment>
        <button className="ui button negative">Delete</button>
        <button className="ui  button">Cancel</button>
      </React.Fragment>
    );
  }
  render() {
    return (
      <div>
        StreamDelete
        <Modal title="Delete Stream" content="Are you sure you want to delete this stream?" actions={this.renderActions()} onDismiss={() => history.push('/')} />

      </div>
    );
  };
  //pass JSX element to action
}
export default StreamDelete;
