import React from "react";
import { connect } from 'react-redux';
import Modal from '../Modal';
import history from '../../history';
import { fetchStream } from '../../actions';
class StreamDelete extends React.Component {
  //cannot assume that the stream has already been loaded up
  componentDidMount() {
    console.log(this.props);
    this.props.fetchStream(this.props.match.params.id);
  }
  renderActions() {
    return (
      <React.Fragment>
        <button className="ui button negative">Delete</button>
        <button className="ui  button">Cancel</button>
      </React.Fragment>
    );
  }

  renderContent(){
    if(!this.props.stream){
      return "Are you sure you want to delte this stream?"
    }

    return "Are you sure you want to delete the stream with title"
  }
  render() {

    return (
      <div>
        StreamDelete
        <Modal title="Delete Stream" content="Are you sure you want to delete this stream?" actions={this.renderActions()} onDismiss={() => history.push('/')} />

      </div>
    );
  };
  //pass JSX element to action
}
const mapStateToProps = (state, ownProps) => {
  return { stream: state.streams[ownProps.match.params.id] }
};
export default connect(mapStateToProps, { fetchStream })(StreamDelete);
